import { db } from "./base";

const checkUserExist = async (uid) => {
  const docRef = db.collection("users").doc(uid);
  const doc = await docRef.get();
  console.log(doc.exists);

  if (!doc.exists) {
    return true;
  } else {
    return false;
  }
};

const addUser = async (uid, val) => {
  const docRef = db.collection("users").doc(uid);
  let userRole = {
    teacher: false,
    student: false,
  };
  if (val) {
    userRole.teacher = true;
  } else {
    userRole.student = true;
  }
  await docRef.set(userRole);
};

const addClass = (code) => {
  const docRef = db.collection("class").doc(code);
  let data = {
    classCode: code,
    className: "Class" + code,
    students: null,
  };

  docRef.set(data);
};

const getUserRole = (uid) => {
  const docRef = db.collection("users").doc(uid);
  const doc = docRef.get();
  if (!doc.exists) {
    console.log("No such document!");
  } else {
    console.log("Document data:", doc.data());
  }
};

export { checkUserExist, addUser, getUserRole };
