import React, { Component } from "react";
import { getAllClasses } from "../utils/Firestore";
import FooterSection from "./FooterSection";
import HeaderSection from "./HeaderSection";
import { Layout, Breadcrumb, List, Card } from "antd";
import SiderBar from "./SideBar";
import { AuthContext } from "../utils/Auth";

const { Content } = Layout;

class Classes extends Component {
  static contextType = AuthContext;
  constructor(props) {
    super(props);
    this.state = { classes: null };
  }
  getClass = async () => {
    const { currentUser } = this.context;
    const data = await getAllClasses(currentUser.uid);
    console.log(data);
    this.setState({ classes: data });
  };
  componentDidMount() {
    this.getClass();
  }
  renderClass = () => {
    const { classes } = this.state;
    if (classes == null) return <h2>No Class</h2>;
    return classes.map((val, idx) => {
      return (
        <List.Item key={idx}>
          <List.Item.Meta
            title={<a href="https://ant.design">{val.className}</a>}
            description="Ant Design, a design language for background applications, is refined by Ant UED Team"
          />
        </List.Item>
      );
    });
  };
  giveRole = () => {
    const { status } = this.context;
    if (status != null) {
      if (status.role) {
        return <div>Teacher</div>;
      } else {
        return <div>Student</div>;
      }
    }
  };
  render() {
    return (
      <Layout style={{ minHeight: "100vh" }}>
        <SiderBar />
        <Layout className="site-layout">
          <HeaderSection />
          <Content style={{ margin: "0 16px" }}>
            <Breadcrumb style={{ margin: "16px 0" }}>
              <Breadcrumb.Item>User</Breadcrumb.Item>
              <Breadcrumb.Item>Bill</Breadcrumb.Item>
            </Breadcrumb>
            <div
              className="site-layout-background"
              style={{ padding: 24, minHeight: 360 }}
            >
              <Card title="Card title">
                <Card
                  type="inner"
                  title="Inner Card title"
                  extra={<a href="#">More</a>}
                >
                  Inner Card content
                </Card>
                <Card
                  style={{ marginTop: 16 }}
                  type="inner"
                  title="Inner Card title"
                  extra={<a href="#">More</a>}
                >
                  Inner Card content
                </Card>
              </Card>
              {this.renderClass()}
              {this.giveRole()}
            </div>
          </Content>
          <FooterSection />
        </Layout>
      </Layout>
    );
  }
}

export default Classes;
