import React, { Component } from "react";
import {
  MDBJumbotron,
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCardTitle,
  MDBIcon,
  MDBCardBody,
  MDBCardText,
} from "mdbreact";

class Main extends Component {
  state = {};

  selectRole = (val) => {
    this.props.history.push({
      pathname: "/login",
      state: { role: val },
    });
  };

  render() {
    return (
      <MDBContainer className="mt-5 text-center">
        <MDBRow>
          <MDBCol>
            <MDBJumbotron>
              <MDBCardBody>
                <MDBCardTitle className="h2">Cheater Checker</MDBCardTitle>
                <p className="blue-text my-4 font-weight-bold">try It now</p>
                <MDBCardText>
                  Become a Teacher or Student using Cheater Checker
                </MDBCardText>
                <hr className="my-4" />
                <div className="pt-2">
                  <MDBBtn
                    color="primary"
                    onClick={() => this.selectRole("student")}
                    className="waves-effect"
                  >
                    Student
                    <MDBIcon far icon="gem" />
                  </MDBBtn>
                  <MDBBtn
                    outline
                    color="primary"
                    onClick={() => this.selectRole("teacher")}
                  >
                    Teacher <MDBIcon icon="download" />
                  </MDBBtn>
                </div>
              </MDBCardBody>
            </MDBJumbotron>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    );
  }
}

export default Main;
