import React, { Component } from "react";
import { getAllClasses, getUserRole } from "../utils/Firestore";
import FooterSection from "./FooterSection";
import HeaderSection from "./HeaderSection";
import { Layout, Card } from "antd";
import SiderBar from "./SideBar";
import { AuthContext } from "../utils/Auth";

const { Content } = Layout;

class Classes extends Component {
  static contextType = AuthContext;
  constructor(props) {
    super(props);
    this.state = { classes: null };
  }
  getClass = async () => {
    const { currentUser } = this.context;
    const data = await getAllClasses(currentUser.uid);

    if (data == null) return null;
    data.map(async (val) => {
      const ownerData = await getUserRole(val.owner);
      val["ownerName"] =
        ownerData["nickname"] != undefined
          ? ownerData.nickname
          : "Not Available";
      console.log(val);
      return val;
    });
    this.setState({ classes: data });
  };
  componentDidMount() {
    this.getClass();
  }
  renderClass = () => {
    const { classes } = this.state;
    if (classes == null) return <h2>No Class</h2>;
    return classes.map((val, idx) => {
      console.log(idx);
      console.log(val);
      return (
        <Card
          key={idx}
          type="inner"
          title={val.className}
          extra={<a href="#">More</a>}
        >
          {val.ownerName}
        </Card>
      );
    });
  };
  giveRole = () => {
    const { status } = this.context;
    if (status != null) {
      if (status.role) {
        return <div>Teacher</div>;
      } else {
        return <div>Student</div>;
      }
    }
  };
  render() {
    return (
      <Layout style={{ minHeight: "100vh" }}>
        <SiderBar />
        <Layout className="site-layout">
          <HeaderSection />
          <Content style={{ margin: "0 16px" }}>
            <Card title="Classes">{this.renderClass()}</Card>
            {this.giveRole()}
          </Content>
          <FooterSection />
        </Layout>
      </Layout>
    );
  }
}

export default Classes;
