import React, { Component } from "react";
import { getAssigments } from "../utils/Firestore";

class Assigment extends Component {
  constructor(props) {
    super(props);
    this.state = { classInfo: this.props.location.state, assigment: null };
  }
  getAllAssigments = async () => {
    const { classInfo } = this.state;
    const classCode = classInfo.classCode;
    const data = await getAssigments(classCode);
    this.setState({ assigment: data });
  };
  renderAssigments = () => {
    const { classInfo, assigment } = this.state;
    if (assigment == null) return null;

    return <div>df</div>;
  };
  componentDidMount() {
    this.getAllAssigments();
  }
  render() {
    return (
      <Layout style={{ minHeight: "100vh" }}>
        <SiderBar />
        <Layout className="site-layout">
          <HeaderSection />
          <Content style={{ margin: "0 16px" }}>
            <Card title="Classes">{this.renderClass()}</Card>
            {this.giveRole()}
          </Content>
          <FooterSection />
        </Layout>
      </Layout>
    );
  }
}

export default Assigment;
