import { db, app } from "./base";
import firebase from "firebase/app";
const checkUserExist = async (uid) => {
  const docRef = db.collection("users").doc(uid);
  const doc = await docRef.get();
  console.log(doc.exists);

  if (!doc.exists) {
    return true;
  } else {
    return false;
  }
};

const addUser = async (uid, val) => {
  const docRef = db.collection("users").doc(uid);
  let userRole = {
    role: null,
  };
  if (val) {
    userRole.role = true;
  } else {
    userRole.role = false;
  }
  await docRef.set(userRole);
};

const addClass = (code) => {
  const docRef = db.collection("classes").doc(code);
  let data = {
    classCode: code,
    className: "Class" + code,
    students: null,
  };

  docRef.set(data);
};
const joinClass = async (code) => {
  const washingtonRef = db.collection("cities").doc("DC");

  // Atomically add a new region to the "regions" array field.
  const unionRes = await washingtonRef.update({
    regions: firebase.firestore.FieldValue.arrayUnion("greater_virginia"),
  });
  const docRef = db.collection("classes").doc(code);
  const r = await docRef.set(
    {
      capital: true,
    },
    { merge: true }
  );
};
const getUserRole = async (uid) => {
  const docRef = db.collection("users").doc(uid);
  const doc = await docRef.get();
  const data = doc.data();
  return data;
};

export { checkUserExist, addUser, getUserRole, addClass, joinClass };
