import React, { Component } from "react";
import { app } from "./base";
import "antd/dist/antd.css";
import { Spin } from "antd";
import { checkUserExist, getUserRole } from "./Firestore";
import { Redirect, withRouter } from "react-router-dom";

export const AuthContext = React.createContext();

class AuthProvider extends Component {
  state = {
    currentUser: null,
    loader: true,
    status: null,
  };
  style = {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "75vh",
  };

  componentDidMount() {
    app.auth().onAuthStateChanged((user) => {
      this.setState({ currentUser: user, loader: false });
      if (user != null) {
        this.checkUser();
      }
    });
  }
  checkUser = async () => {
    let { currentUser, status } = this.state;
    const { history } = this.props;
    if (currentUser != null && status == null) {
      let res = await checkUserExist(currentUser.uid);
      let temp = "";
      if (res) {
        temp = "/selectrole";
      } else {
        temp = "/";
      }
      history.push(temp);
      this.setState({ currentUser });
    }
  };

  getRole = async () => {
    const { currentUser, status } = this.state;
    if (currentUser != null && status == null) {
      const data = await getUserRole(currentUser.uid);
      this.setState({ status: data });
    }
  };
  componentDidUpdate() {
    this.checkUser();
    //this.getRole();
  }
  render() {
    const { currentUser, loader, status } = this.state;
    if (loader) {
      return (
        <div style={this.style}>
          <Spin size="large" tip="Loading..." />
        </div>
      );
    } else {
      return (
        <AuthContext.Provider value={{ currentUser, status }}>
          {this.props.children}
        </AuthContext.Provider>
      );
    }
  }
}

export default withRouter(AuthProvider);
