import React, { Component } from "react";
import { withRouter } from "react-router";
import { app } from "../utils/base.js";
import firebase from "firebase";
import { AuthContext } from "../utils/Auth.js";
import { checkUserExist, addUser } from "../utils/Firestore";

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = { role: null };
  }

  componentDidMount() {
    if (this.props.location.state) {
      this.setState({ role: this.props.location.state.role });
    }
  }
  googleLogin = async () => {
    const { role } = this.state;
    var provider = new firebase.auth.GoogleAuthProvider();
    app
      .auth()
      .signInWithRedirect(provider)
      .then(async (result) => {
        // The signed-in user info.
        var user = result.user;
        if (!role) {
        }
        await checkUserExist(user.uid);
        await addUser(user.uid, role);
      })
      .catch((error) => {
        var errorMessage = error.message;
      });
  };
  render() {
    console.log(this.state);
    return (
      <div>
        <h1>Log in</h1>
        <button onClick={this.googleLogin}>OnClick</button>
      </div>
    );
  }
}

export default Login;
