import React, { Component } from "react";
import { MDBContainer, MDBRow, MDBCol } from "mdbreact";

class Main extends Component {
  state = {};

  selectRole = (val) => {
    this.props.history.push({
      pathname: "/login",
      state: { role: val },
    });
  };

  render() {
    return (
      <MDBContainer className="mt-5 text-center">
        <MDBRow>
          <MDBCol></MDBCol>
        </MDBRow>
      </MDBContainer>
    );
  }
}

export default Main;
