import { db } from "./base";

const checkUserExist = async (uid) => {
  console.log(uid);
  const docRef = db.collection("users").doc(uid);
  console.log(docRef.exist);

  if (!docRef.exists) {
    console.log("No such document!");
  } else {
    console.log("Document data:", docRef.data());
  }
  //   await docRef.set({
  //     teacher: true,
  //     student: false,
  //   });
};

export default checkUserExist;
