import React, { Component } from "react";
import { app } from "./base";
import "antd/dist/antd.css";
import { Spin } from "antd";
import { getUserRole } from "./Firestore";
const AuthContext = React.createContext();

class AuthProvider extends Component {
  state = { currentUser: null, loader: true, status: null };
  style = {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "75vh",
  };

  componentDidMount() {
    app.auth().onAuthStateChanged((user) => {
      this.setState({ currentUser: user, loader: false });
    });
  }
  getRole = async () => {
    const { currentUser, status } = this.state;
    if (currentUser != null && status == null) {
      const data = await getUserRole(currentUser.uid);
      console.log("ddff");
      console.log(data);
      this.setState({ status: data });
    }
  };
  componentDidUpdate() {
    this.getRole();
  }
  render() {
    const { currentUser, loader, status } = this.state;
    if (loader) {
      return (
        <div style={this.style}>
          <Spin size="large" tip="Loading..." />
        </div>
      );
    } else {
      return (
        <AuthContext.Provider value={{ currentUser, status }}>
          {this.props.children}
        </AuthContext.Provider>
      );
    }
  }
}

export { AuthProvider, AuthContext };
