import React, { Component } from "react";
import { app } from "./base";
import { checkUserExist } from "./Firestore";
import { useHistory } from "react-router-dom";

const history = useHistory();
const AuthContext = React.createContext();

class AuthProvider extends Component {
  state = { currentUser: null, loader: true };

  componentDidMount() {
    app.auth().onAuthStateChanged((user) => {
      this.setState({ currentUser: user, loader: false });
    });
  }
  checkUser = async () => {
    const { currentUser } = this.state;
    console.log(currentUser);
    if (currentUser != null) {
      let res = await checkUserExist(currentUser.uid);
      if (res) {
        history.push({
          pathname: "/selectrole",
          state: { uid: currentUser.uid },
        });
      } else {
        history.push("/");
      }
    }
  };
  componentDidUpdate() {
    this.checkUser();
  }
  render() {
    const { currentUser, loader } = this.state;
    if (loader) {
      return <div>Loader....</div>;
    } else {
      return (
        <AuthContext.Provider value={{ currentUser }}>
          {this.props.children}
        </AuthContext.Provider>
      );
    }
  }
}

export { AuthProvider, AuthContext };
