import { db, app } from "./base";

const checkUserExist = async (uid) => {
  const docRef = db.collection("users").doc(uid);
  const doc = await docRef.get();
  console.log(doc.exists);

  if (!doc.exists) {
    return true;
  } else {
    return false;
  }
};

const addUser = async (uid, val) => {
  const docRef = db.collection("users").doc(uid);
  let userRole = {
    role: null,
  };
  if (val) {
    userRole.role = true;
  } else {
    userRole.role = false;
  }
  await docRef.set(userRole);
};

const addClass = (code) => {
  const docRef = db.collection("classes").doc(code);
  let data = {
    classCode: code,
    className: "Class" + code,
    students: null,
  };

  docRef.set(data);
};
const joinClass = async (code) => {
  const docRef = db.collection("classes").doc(code).collection("dsd");
  console.log("dffdfdfdf");
  // const unionRes = await docRef.update({
  //   regions: app.firestore.FieldValue.arrayUnion("sdsd"),
  // });
};
const getUserRole = async (uid) => {
  const docRef = db.collection("users").doc(uid);
  const doc = await docRef.get();
  const data = doc.data();
  return data;
};

export { checkUserExist, addUser, getUserRole, addClass, joinClass };
