import React, { Component } from "react";
import "./ui.css";
import { app } from "../utils/base.js";
import firebase from "firebase";
import { AuthContext } from "../utils/Auth.js";
import { checkUserExist } from "../utils/Firestore";
import Navbar from "./Navbar";
import { Layout, Steps, Button } from "antd";
import FooterPage from "./footer";
const { Content } = Layout;
const { Step } = Steps;

class ExtraInfo extends Component {
  state = {};
  render() {
    return (
      <Layout style={{ minHeight: "100vh" }}>
        <Navbar />
        <Content style={{ padding: "5vh" }}>
          <div className="site-layout-content">
            <Steps size="small" current={2}>
              <Step title="Login" />
              <Step title="Select Role" />
              <Step title="Extra Info" />
            </Steps>
            <Form
              form={form}
              layout="vertical"
              initialValues={{
                requiredMark,
              }}
              onValuesChange={onRequiredTypeChange}
              requiredMark={requiredMark}
            >
              <Form.Item label="Required Mark" name="requiredMark">
                <Radio.Group>
                  <Radio.Button value="optional">Optional</Radio.Button>
                  <Radio.Button value={true}>Required</Radio.Button>
                  <Radio.Button value={false}>Hidden</Radio.Button>
                </Radio.Group>
              </Form.Item>
              <Form.Item
                label="Field A"
                required
                tooltip="This is a required field"
              >
                <Input placeholder="input placeholder" />
              </Form.Item>
              <Form.Item
                label="Field B"
                tooltip={{
                  title: "Tooltip with customize icon",
                  icon: <InfoCircleOutlined />,
                }}
              >
                <Input placeholder="input placeholder" />
              </Form.Item>
              <Form.Item>
                <Button type="primary">Submit</Button>
              </Form.Item>
            </Form>
            <Button
              size="large"
              style={{ float: "center", margin: "10px" }}
              onClick={() => this.props.history.push("/")}
              type="primary"
            >
              Skip
            </Button>
          </div>
        </Content>
        <FooterPage />
      </Layout>
    );
  }
}

export default ExtraInfo;
