import { db } from "./base";

const checkUserExist = async (uid) => {
  console.log(uid);
  const docRef = db.collection("users").doc(uid);
  console.log(docRef.exist);

  if (!docRef.exist) {
    return true;
  }
  return false;

  //   await docRef.set({
  //     teacher: true,
  //     student: false,
  //   });
};

export default checkUserExist;
