    regions: admin.firestore.FieldValue.arrayUnion("sdsd"),
  });
};
const getUserRole = async (uid) => {
  const docRef = db.collection("users").doc(uid);
  const doc = await docRef.get();
  const data = doc.data();
  return data;
};

export { checkUserExist, addUser, getUserRole, addClass };
