import React, { Component } from "react";
import { withRouter } from "react-router";
import { app } from "../utils/base.js";
import firebase from "firebase";
import { AuthContext } from "../utils/Auth.js";
import { checkUserExist, addUser } from "../utils/Firestore";

class Login extends Component {
  static contextType = AuthContext;
  googleLogin = async () => {
    var provider = new firebase.auth.GoogleAuthProvider();
    app.auth().signInWithRedirect(provider);
  };

  checkUser = async (currentUser) => {
    console.log("dfdf");
    let res = await checkUserExist(currentUser.uid);
    if (res) {
      this.props.history.push({
        pathname: "/selectrole",
        state: { uid: currentUser.uid },
      });
    } else {
      this.props.history.push("/");
    }
  };

  componentDidUpdate() {
    const { currentUser } = this.context;
    this.checkUser(currentUser);
  }

  render() {
    return (
      <div>
        <h1>Log in</h1>
        <button onClick={this.googleLogin}>OnClick</button>
      </div>
    );
  }
}

export default Login;
