import { db } from "./base";

const checkUserExist = async (uid) => {
  console.log(uid);
  const docRef = db.collection("users").doc(uid);
  const doc = await docRef.get();
  console.log(doc.exists);

  if (doc.exists == false) {
    console.log("fffd");
    return true;
  } else {
    console.log("sdsd");
    return false;
  }
  await docRef.set({
    teacher: true,
    student: true,
  });
};

export default checkUserExist;
