import React, { Component } from "react";
import { addUser } from "../utils/Firestore";

class ChooseRole extends Component {
  state = {};
  addRole = (val) => {
    console.log(val);
    console.log(this.props.location.state.uid);
    const userUid = this.props.location.state.uid;
    if (val == "teacher") {
      addUser(userUid, true);
    } else {
      addUser(userUid, false);
    }
  };
  render() {
    return (
      <div>
        <button onClick={this.addRole("teacher")}>Teacher</button>
        <button onClick={this.addRole("student")}>Student</button>
      </div>
    );
  }
}

export default ChooseRole;
